//SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.5.0 <0.9.0;
pragma experimental ABIEncoderV2;

contract Medication{
    uint public perscriptionCount=0;

 struct Perscription{
    uint id;
    string[] medications;
    string[] doses;
    string[] intakePeriod;
    string followUpAppointment;
    string refferalDoctor;
    string[] labTests;

 }

 mapping(uint=>Perscription) public perscriptions; //store patients in db aka blockchain ,treat this like a model
//keyword memory temporarly stores data in vars
 function createPatient(string[] memory _medications,string[] memory _doses, 
    string[] memory _intakePeriod,      
    string memory _followUpAppointment,string memory _referralDoctor ,
    string[] memory _labTests) public {
     perscriptionCount++;    

     perscriptions[perscriptionCount]=Perscription(perscriptionCount,_medications,_doses,_intakePeriod,_followUpAppointment,
     _referralDoctor,_labTests);
 } 

}
